+ isListening: Boolean,
+ errorMessage: String,
+ onStartListening: () -> Unit,
+ onStopListening: () -> Unit,
+ onSpeakText: (String) -> Unit,
+ onClearMessages: () -> Unit
+) {
+ val listState = rememberLazyListState()
+ val coroutineScope = rememberCoroutineScope()
+
+ LaunchedEffect(messages.size) {
+ if (messages.isNotEmpty()) {
+ coroutineScope.launch {
+ listState.animateScrollToItem(messages.size - 1)
+ }
+ }
+ }
+
+ Scaffold(
+ topBar = {
+ TopAppBar(
+ title = { Text("Voice Assistant", fontWeight = FontWeight.Bold) },
+ colors = TopAppBarDefaults.topAppBarColors(
+ containerColor = MaterialTheme.colorScheme.primary,
+ titleContentColor = MaterialTheme.colorScheme.onPrimary
+ )
+ )
+ }
+ ) { paddingValues ->
+ Column(
+ modifier = Modifier
+ .fillMaxSize()
+ .padding(paddingValues)
+ .padding(bottom = 16.dp)
+ ) {
+ LazyColumn(
+ state = listState,
+ modifier = Modifier.weight(1f),
+ contentPadding = PaddingValues(16.dp),
+ verticalArrangement = Arrangement.spacedBy(12.dp)
+ ) {
+ items(messages) { (sender, text) ->
+ MessageBubble(sender = sender, text = text, onSpeakText = onSpeakText)
+ }
+ }
+
+ if (errorMessage.isNotEmpty()) {
+ Card(
+ modifier = Modifier
+ .fillMaxWidth()
+ .padding(horizontal = 16.dp),
+ colors = CardDefaults.cardColors(
+ containerColor = MaterialTheme.colorScheme.errorContainer
+ )
+ ) {
+ Text(
+ text = errorMessage,
+ modifier = Modifier.padding(16.dp),
+ color = MaterialTheme.colorScheme.onErrorContainer,
+ textAlign = TextAlign.Center,
+ fontWeight = FontWeight.Medium
+ )
+ }
+ Spacer(modifier = Modifier.height(8.dp))
+ }
+
+ Column(
+ modifier = Modifier.fillMaxWidth(),
+ horizontalAlignment = Alignment.CenterHorizontally,
+ verticalArrangement = Arrangement.Center
+ ) {
+ FloatingActionButton(
+ onClick = {
+ if (isListening) {
+ onStopListening()
+ } else {
+ onStartListening()
+ }
+ },
+ containerColor = when {
+ isListening -> MaterialTheme.colorScheme.error
+ else -> MaterialTheme.colorScheme.primary
+ },
+ contentColor = Color.White
+ ) {
+ Icon(Icons.Default.Mic, contentDescription = "Microphone")
+ }
+
+ Text(
+ text = when {
+ isListening -> "Listening... Speak now!"
+ else -> "Tap the mic to talk"
+ },
+ modifier = Modifier.padding(top = 8.dp),
+ textAlign = TextAlign.Center,
+ style = MaterialTheme.typography.titleMedium
+ )
+ }
+ }
+ }
+}
+
+@Composable
+fun MessageBubble(sender: String, text: String, onSpeakText: (String) -> Unit) {
+ val isUser = sender == "user"
+ Row(
+ modifier = Modifier.fillMaxWidth(),
+ horizontalArrangement = if (isUser) Arrangement.End else Arrangement.Start
+ ) {
+ Card(
+ modifier = Modifier.widthIn(max = 300.dp),
+ shape = RoundedCornerShape(16.dp),
+ colors = CardDefaults.cardColors(
+ containerColor = if (isUser) MaterialTheme.colorScheme.primaryContainer else MaterialTheme.colorScheme.surfaceVariant
+ )
+ ) {
+ Row(
+ modifier = Modifier.padding(12.dp),
+ verticalAlignment = Alignment.CenterVertically
+ ) {
+ Text(
+ text,
+ modifier = Modifier.weight(1f),
+ style = MaterialTheme.typography.bodyLarge
+ )
+ if (!isUser) {
+ Spacer(Modifier.width(8.dp))
+ IconButton(onClick = { onSpeakText(text) }, modifier = Modifier.size(24.dp)) {
+ Icon(
+ Icons.Default.VolumeUp,
+ "Speak",
+ tint = MaterialTheme.colorScheme.onSurfaceVariant
+ )
+ }
+ }
+ }
+ }
+ }
+}
+
+class MainViewModelFactory(private val activity: ComponentActivity) :
+ ViewModelProvider.NewInstanceFactory() {
+ override fun <T : ViewModel> create(modelClass: Class<T>): T {
+ return MainViewModel(activity) as T
+ }
+}
