+
+ // Music/media
+ command.contains(Regex("\\b(play|music|song|playlist|video)\\b")) ->
+ playMedia(command)
+
+ // Default response for unrecognized commands
+ else -> AssistantResponse.Text(getDefaultResponse(input))
+ }
+ }
+
+ private fun getGreeting(): String {
+ val hour = Calendar.getInstance().get(Calendar.HOUR_OF_DAY)
+ return when {
+ hour < 12 -> "Good morning! How can I assist you today?"
+ hour < 17 -> "Good afternoon! What can I help you with?"
+ hour < 21 -> "Good evening! How may I help you?"
+ else -> "Hello! I'm here to help you anytime."
+ }
+ }
+
+ private fun getCurrentTime(): String {
+ val formatter = SimpleDateFormat("h:mm a", Locale.getDefault())
+ return "The current time is ${formatter.format(Date())}."
+ }
+
+ private fun getCurrentDate(): String {
+ val formatter = SimpleDateFormat("EEEE, MMMM d, yyyy", Locale.getDefault())
+ return "Today is ${formatter.format(Date())}."
+ }
+
+ private fun getWeatherResponse(): String {
+ // In a real app, you'd integrate with a weather API
+ val responses = listOf(
+ "I'd love to help with weather information! For real-time weather, I recommend checking your weather app or asking about a specific location.",
+ "Weather information requires location access and a weather service. Would you like me to help you open your weather app?",
+ "I don't have access to current weather data, but I can help you with many other things!"
+ )
+ return responses.random()
+ }
