+ private fun calculateMath(command: String): String {
+ return try {
+ // Simple math parser for basic operations
+ val mathPattern =
+ Regex("(\\d+(?:\\.\\d+)?)\\s*(\\+|\\-|\\*|\\/|\\×|\\÷)\\s*(\\d+(?:\\.\\d+)?)")
+ val match = mathPattern.find(command)
+
+ if (match != null) {
+ val num1 = match.groupValues[1].toDouble()
+ val operator = match.groupValues[2]
+ val num2 = match.groupValues[3].toDouble()
+
+ val result = when (operator) {
+ "+", "plus" -> num1 + num2
+ "-", "minus" -> num1 - num2
+ "*", "×", "multiply", "times" -> num1 * num2
+ "/", "÷", "divide" -> if (num2 != 0.0) num1 / num2 else Double.NaN
+ else -> Double.NaN
+ }
+
+ if (result.isNaN()) {
+ "I can't perform that calculation. Please check your input."
+ } else {
+ "${num1} ${operator} ${num2} equals ${
+ if (result == result.toInt().toDouble()) result.toInt() else result
+ }"
+ }
+ } else {
+ "I can help with basic math! Try saying something like 'calculate 25 plus 30' or '15 times 4'."
+ }
+ } catch (e: Exception) {
+ "I couldn't understand that math problem. Try a simpler format like '10 plus 5' or '20 divided by 4'."
+ }
+ }
+
+ private fun convertUnits(command: String): String {
+ return when {
+ command.contains("feet") && command.contains("meter") -> {
+ val feet = Regex("(\\d+(?:\\.\\d+)?)\\s*feet").find(command)?.groupValues?.get(1)
+ ?.toDoubleOrNull()
+ if (feet != null) {
+ val meters = feet * 0.3048
+ "$feet feet equals ${String.format("%.2f", meters)} meters"
+ } else "Please specify the number of feet to convert."
+ }
+
+ command.contains("meter") && command.contains("feet") -> {
+ val meters = Regex("(\\d+(?:\\.\\d+)?)\\s*meter").find(command)?.groupValues?.get(1)
+ ?.toDoubleOrNull()
+ if (meters != null) {
+ val feet = meters * 3.28084
+ "$meters meters equals ${String.format("%.2f", feet)} feet"
+ } else "Please specify the number of meters to convert."
+ }
+
+ command.contains("celsius") && command.contains("fahrenheit") -> {
+ val celsius =
+ Regex("(\\d+(?:\\.\\d+)?)\\s*celsius").find(command)?.groupValues?.get(1)
+ ?.toDoubleOrNull()
+ if (celsius != null) {
+ val fahrenheit = (celsius * 9 / 5) + 32
+ "$celsius°C equals ${String.format("%.1f", fahrenheit)}°F"
+ } else "Please specify the Celsius temperature to convert."
+ }
+
+ command.contains("fahrenheit") && command.contains("celsius") -> {
+ val fahrenheit =
+ Regex("(\\d+(?:\\.\\d+)?)\\s*fahrenheit").find(command)?.groupValues?.get(1)
+ ?.toDoubleOrNull()
+ if (fahrenheit != null) {
+ val celsius = (fahrenheit - 32) * 5 / 9
+ "$fahrenheit°F equals ${String.format("%.1f", celsius)}°C"
+ } else "Please specify the Fahrenheit temperature to convert."
+ }
+
+ else -> "I can convert between feet/meters and Celsius/Fahrenheit. Try saying 'convert 10 feet to meters' or '25 celsius to fahrenheit'."
+ }
+ }
+
+ private fun setAlarm(command: String): AssistantResponse {
+ return try {
+ val intent = Intent(AlarmClock.ACTION_SET_ALARM)
+ intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK)
+
+ // Extract time if mentioned
+ val timePattern = Regex(
+ "(\\d{1,2})(?::(\\d{2}))?"
+ + "(?:\\s*(am|pm|AM|PM))?"
+ + "|(?:in\\s+(\\d+)\\s*(minute|minutes|hour|hours))"
+ )
+ val match = timePattern.find(command)
+
+ if (match != null) {
+ val hour = match.groupValues[1].toIntOrNull()
+ val minute = match.groupValues[2].toIntOrNull() ?: 0
+ val amPm = match.groupValues[3].lowercase()
+ val duration = match.groupValues[4].toIntOrNull()
+ val unit = match.groupValues[5]
+
+ when {
+ hour != null -> {
+ val finalHour = when {
+ amPm == "pm" && hour != 12 -> hour + 12
+ amPm == "am" && hour == 12 -> 0
+ else -> hour
+ }
+ intent.putExtra(AlarmClock.EXTRA_HOUR, finalHour)
+ intent.putExtra(AlarmClock.EXTRA_MINUTES, minute)
+ intent.putExtra(AlarmClock.EXTRA_MESSAGE, "Voice Assistant Alarm")
+ }
+
+ duration != null -> {
+ val minutes = when (unit) {
+ "hour", "hours" -> duration * 60
+ else -> duration
+ }
+ intent.putExtra(
+ AlarmClock.EXTRA_LENGTH,
+ minutes * 60 * 1000
+ ) // milliseconds
+ intent.putExtra(AlarmClock.EXTRA_MESSAGE, "Voice Assistant Timer")
+ }
+ }
+ }
+
+ AssistantResponse.Action("Opening alarm app...", intent)
+ } catch (e: Exception) {
+ AssistantResponse.Text("I can help you set alarms! Try saying 'set alarm for 7:30 AM' or 'set timer for 10 minutes'.")
+ }
+ }
+
+ private fun openApp(command: String): AssistantResponse {
+ val intent = when {
+ command.contains("camera") -> Intent(MediaStore.ACTION_IMAGE_CAPTURE)
+ command.contains("gallery") || command.contains("photos") -> Intent(
+ Intent.ACTION_VIEW,
+ MediaStore.Images.Media.EXTERNAL_CONTENT_URI
+ )
+
+ command.contains("contacts") -> Intent(
+ Intent.ACTION_VIEW,
+ ContactsContract.Contacts.CONTENT_URI
+ )
+
+ command.contains("calendar") -> Intent(Intent.ACTION_VIEW).setData(CalendarContract.CONTENT_URI)
+ command.contains("settings") -> Intent(android.provider.Settings.ACTION_SETTINGS)
+ else -> null
+ }
+
+ return if (intent != null) {
+ intent.addFlags(Intent.FLAG_ACTIVITY_NEW_TASK)
+ AssistantResponse.Action("Opening app...", intent)
+ } else {
+ AssistantResponse.Text("I can help you open camera, gallery, contacts, calendar, or settings. Which app would you like to open?")
+ }
+ }
+
+ private fun performSearch(command: String): AssistantResponse {
+ val searchQuery = command.replace(
+ Regex("\\b(search|google|find|look up|what is|who is|tell me about)\\b"),
+ ""
+ ).trim()
+ val intent = Intent(Intent.ACTION_WEB_SEARCH).apply {
+ putExtra("query", searchQuery)
+ addFlags(Intent.FLAG_ACTIVITY_NEW_TASK)
+ }
+ return AssistantResponse.Action("Searching for: $searchQuery", intent)
+ }
+
+ private fun getDirections(command: String): AssistantResponse {
+ val destination =
+ command.replace(Regex("\\b(navigate|directions|route|how to get|take me to)\\b"), "")
+ .trim()
+ val intent =
+ Intent(Intent.ACTION_VIEW, Uri.parse("google.navigation:q=$destination")).apply {
+ addFlags(Intent.FLAG_ACTIVITY_NEW_TASK)
+ }
+ return AssistantResponse.Action("Getting directions to: $destination", intent)
+ }
+
+ private fun playMedia(command: String): AssistantResponse {
+ val intent = Intent(MediaStore.INTENT_ACTION_MEDIA_PLAY_FROM_SEARCH).apply {
+ addFlags(Intent.FLAG_ACTIVITY_NEW_TASK)
+ }
+ return AssistantResponse.Action("Opening music player...", intent)
+ }
+
+ private fun getAppreciationResponse(): String {
+ val responses = listOf(
+ "You're very welcome! I'm always here to help.",
+ "My pleasure! Is there anything else I can assist you with?",
+ "Happy to help! What else can I do for you?",
+ "You're welcome! I'm glad I could be helpful.",
+ "Anytime! I'm here whenever you need assistance."
+ )
+ return responses.random()
+ }
+
+ private fun getGoodbyeResponse(): String {
+ val responses = listOf(
+ "Goodbye! Have a wonderful day!",
+ "See you later! Take care!",
+ "Until next time! Have a great day!",
+ "Farewell! I'm here whenever you need me.",
+ "Goodbye! It was great helping you today!"
+ )
+ return responses.random()
+ }
+
+ private fun getDefaultResponse(input: String): String {
+ val responses = listOf(
+ "I heard you say: '$input'. I'm still learning, but I can help with time, weather, calculations, setting alarms, opening apps, and much more!",
+ "Interesting! While I work on understanding '$input' better, I can help you with math, unit conversions, jokes, quotes, and various device functions.",
+ "I'm not sure about '$input' yet, but I'm great with time queries, weather information, calculations, and controlling your device. What would you like to try?",
+ "That's a good question about '$input'! I can assist with setting alarms, opening apps, doing math, getting directions, and many other tasks. How can I help?"
+ )
+ return responses.random()
+ }
