- val num1 = match.groupValues[1].toDouble()
- val operator = match.groupValues[2]
- val num2 = match.groupValues[3].toDouble()
-
- val result = when (operator) {
- "+", "plus" -> num1 + num2
- "-", "minus" -> num1 - num2
- "*", "×", "multiply", "times" -> num1 * num2
- "/", "÷", "divide" -> if (num2 != 0.0) num1 / num2 else Double.NaN
- else -> Double.NaN
- }
-
- if (result.isNaN()) {
- "I can't perform that calculation. Please check your input."
- } else {
- "${num1} ${operator} ${num2} equals ${
- if (result == result.toInt().toDouble()) result.toInt() else result
- }"
- }
